import React, { useState } from 'react';
import { Link, Route, Routes, useLocation } from 'react-router-dom';
import './HomePage.css'; // Import file CSS riêng cho HomePage

const HomePage = () => {
  const [showAccountMenu, setShowAccountMenu] = useState(false);
  const location = useLocation();
  const [user, setUser] = useState(localStorage.getItem('user') || 'Guest');
  const handleAccountClick = () => {
    setShowAccountMenu(!showAccountMenu);
  };

  const handleLogout = () => {
    // Logic để logout
    console.log('Logged out');
  };

return (
    <div className="homepage-container">
        <header className="header">
            <div className="header-left">
                <h1><i className="fas fa-store"></i> Shop bóng bàn Mạnh Dũng</h1>
            </div>
            <div className="header-right">
                <i className="fas fa-user-circle" onClick={handleAccountClick}></i>
                {showAccountMenu && (
                    <div className="account-menu">
                        <p>User: {user}</p>
                        <button onClick={handleLogout}>Logout</button>
                    </div>
                )}
            </div>
        </header>
        <nav className="navbar">
            <ul>
                <li><Link to="/home-page/nhap-hang"><i className="fas fa-truck-loading"></i> Nhập hàng</Link></li>
                <li><Link to="/home-page/quan-ly-kho"><i className="fas fa-warehouse"></i> Quản lý kho hàng</Link></li>
                <li><Link to="/home-page/quan-ly-don-hang"><i className="fas fa-receipt"></i> Quản lý đơn hàng</Link></li>
                <li><Link to="/home-page/doanh-thu"><i className="fas fa-chart-line"></i> Doanh thu</Link></li>
            </ul>
        </nav>
        <main className="main-content">
            <Routes>
                <Route path="/home-page/nhap-hang" element={<NhapHang />} />
                <Route path="/home-page/quan-ly-kho" element={<QuanLyKho />} />
                <Route path="/home-page/quan-ly-don-hang" element={<QuanLyDonHang />} />
                <Route path="/home-page/doanh-thu" element={<DoanhThu />} />
            </Routes>
        </main>
    </div>
);
};

const NhapHang = () => (
  <section>
    <h2>Nhập hàng</h2>
    <p>Thông tin về nhập hàng...</p>
  </section>
);

const QuanLyKho = () => (
  <section>
    <h2>Quản lý kho hàng</h2>
    <p>Thông tin về quản lý kho hàng...</p>
  </section>
);

const QuanLyDonHang = () => (
  <section>
    <h2>Quản lý đơn hàng</h2>
    <p>Thông tin về quản lý đơn hàng...</p>
  </section>
);

const DoanhThu = () => (
  <section>
    <h2>Doanh thu</h2>
    <p>Thông tin về doanh thu...</p>
  </section>
);

export default HomePage;
