import React, { useState } from 'react';

const AdminLogin = () => {
  const [account, setAccount] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    // Handle login logic here
    console.log('Account:', account);
    console.log('Password:', password);
  };

  return (
    <div id="main">
        <div id="header">
            <div class="footer-heading">
                shop bóng bàn Mạnh Dũng
             </div>
        </div>
        <div id="content" style={{ marginBottom: 0, paddingBottom: 0 }}>
      <div className="log-in">
        <div className="log-in-box">
          <form onSubmit={handleLogin}>
            <label htmlFor="account">Tên đăng nhập: <br /></label>
            <input 
              type="text" 
              name="account" 
              id="account" 
              value={account} 
              onChange={(e) => setAccount(e.target.value)} 
            /> 
            <br />
            <label htmlFor="password">Mật khẩu: <br /></label>
            <input 
              type="password" 
              name="password" 
              id="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
            />
            <br />
            <button type="submit" className="btn-log-in">Đăng nhập</button>
          </form>
        </div>
      </div>
    </div>

    </div>
   
  );
};

export default AdminLogin;
