import React, { useState } from 'react';
import axios from 'axios';

const AdminLogin = () => {
  const [account, setAccount] = useState('');
  const [password, setPassword] = useState('');

const handleLogin = async (e) => {
    e.preventDefault();
    try {
        const response = await axios.get('http://localhost:3002/api/get');
        if (response.data.admin_user === account) {
            if (response.data.admin_password === password) {
                console.log('Login successful!');
            } else {
                console.log('Incorrect password!');
            }
        } else {
            console.log('Account not found!');
        }
        console.log('Response:', response);
    } catch (error) {
        console.error('Error during login:', error);
    }
    console.log('Account:', account);
    console.log('Password:', password);
};

  return (
    <div id="main">
        <div id="header">
            <div class="footer-heading">
                shop bóng bàn Mạnh Dũng
             </div>
        </div>
        <div id="content" style={{ marginBottom: 0, paddingBottom: 0 }}>
      <div className="log-in">
        <div className="log-in-box">
          <form onSubmit={handleLogin}>
            <label htmlFor="account">Tên đăng nhập: <br /></label>
            <input 
              type="text" 
              name="account" 
              id="account" 
              value={account} 
              onChange={(e) => setAccount(e.target.value)} 
            /> 
            <br />
            <label htmlFor="password">Mật khẩu: <br /></label>
            <input 
              type="password" 
              name="password" 
              id="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
            />
            <br />
            <button type="submit" className="btn-log-in">Đăng nhập</button>
          </form>
        </div>
      </div>
        </div>
        <div id="footer">
             <div class="footer-heading">
                shop bóng bàn Mạnh Dũng
             </div>
             <div class="contact-inf">
                <div class="icon-contact-footer"><a href="https://www.facebook.com/0204hoangdung"><i class="fa-brands fa-facebook-f"></i></a></div>
                <div class="icon-contact-footer"><a href=""><i class="fa-brands fa-google"></i></a></div>
                <div class="icon-contact-footer"><a href=""><i class="fa-brands fa-twitter"></i></a></div>
                <div class="call-contact-footer">
                <i class="fa-solid fa-phone"></i>
                <p class="mobile-footer">hotline: 0359090735</p>
                </div>
             </div>
             <div class="end-footer">       
             </div>
        </div>

    </div>
   
  );
};

export default AdminLogin;
