import React, { useState } from 'react';
import axios from 'axios';

const AdminLogin = () => {
  const [admin, setAdmin] = useState({ admin_user: '', admin_password: '' });
  const [message, setMessage] = useState(''); // State để lưu trữ thông báo

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAdmin({ ...admin, [name]: value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get('http://localhost:3002/api/get-admin', { params: admin });
      if (!response.data || response.data.length === 0) {
        setMessage('Không tìm thấy tên đăng nhập');
        return;
      } else {
        const adminData = response.data[0];
        if (adminData.admin_password === admin.admin_password) {
          setMessage('Đăng nhập thành công');
        } else {
          setMessage('Sai mật khẩu');
        }
      }
    } catch (error) {
      setMessage('Có lỗi xảy ra trong quá trình đăng nhập');
      console.error('Error during login:', error);
    }
  };

  return (
    <div id="main">
      <div id="header">
        <h1>Admin Login</h1>
      </div>
      <div id="content" style={{ marginBottom: 0, paddingBottom: 0 }}>
        <div className="log-in">
          <div className="log-in-box">
            <form onSubmit={handleLogin}>
              <label htmlFor="admin_user">Tên đăng nhập: <br /></label>
              <input 
                type="text" 
                name="admin_user" 
                id="admin_user" 
                value={admin.admin_user} 
                onChange={handleChange} 
              /> 
              <br />
              <label htmlFor="admin_password">Mật khẩu: <br /></label>
              <input 
                type="password" 
                name="admin_password" 
                id="admin_password" 
                value={admin.admin_password} 
                onChange={handleChange} 
              />
              <br />
              <button type="submit" className="btn-log-in">Đăng nhập</button>
            </form>
            {message && <p>{message}</p>} {/* Hiển thị thông báo */}
          </div>
        </div>
      </div>
      <div id="footer">
        {/* Footer content */}
      </div>
    </div>
  );
};

export default AdminLogin;
